while True:
    data = requests.get(url).json() 
    feed = data['feeds'][0]                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
    print("Field 1:", feed['field1'], "| Field 2 :", feed['field2'], "| Field 3 :", feed['field3'], "| Field 4 :", feed['field4'], "| Field 5 :", feed['field5'], "| Field 6 :", feed['field6'])
    time.sleep(5)
  